For those who care:
```c#
// WhatsApp.FunXMPP.Connection
public void SendBroadcastMessage(FunXMPP.FMessage message, string[] recipients)
{
	if (message.media_wa_type != FunXMPP.FMessage.Type.Undefined)
	{
		this.SendMessageWithMedia(message, recipients);
		return;
	}
	this.SendMessageWithBody(message, recipients);
}

internal void SendMessageWithBody(FunXMPP.FMessage message, string[] recipients = null)
{
	FunXMPP.ProtocolTreeNode child = new FunXMPP.ProtocolTreeNode("body", null, message.data);
	this.Login.TreeNodeWriter.Write(FunXMPP.Connection.GetMessageNode(message, child, recipients), this.AsyncHandler);
}

internal static FunXMPP.ProtocolTreeNode GetMessageNode(FunXMPP.FMessage message, FunXMPP.ProtocolTreeNode child, string[] recipients = null)
{
	FunXMPP.ProtocolTreeNode protocolTreeNode = (recipients == null) ? null : FunXMPP.Connection.CreateBroadcastNode(recipients);
	FunXMPP.ProtocolTreeNode protocolTreeNode2 = new FunXMPP.ProtocolTreeNode("x", new FunXMPP.KeyValue[]
	{
		new FunXMPP.KeyValue("xmlns", "jabber:x:event")
	}, new FunXMPP.ProtocolTreeNode("server", null));
	FunXMPP.ProtocolTreeNode[] array = Enumerable.ToArray<FunXMPP.ProtocolTreeNode>(Enumerable.Where<FunXMPP.ProtocolTreeNode>(new FunXMPP.ProtocolTreeNode[]
	{
		protocolTreeNode,
		protocolTreeNode2,
		child
	}, (FunXMPP.ProtocolTreeNode n) => n != null));
	return new FunXMPP.ProtocolTreeNode("message", new FunXMPP.KeyValue[]
	{
		new FunXMPP.KeyValue("to", message.key.remote_jid),
		new FunXMPP.KeyValue("type", "chat"),
		new FunXMPP.KeyValue("id", message.key.id)
	}, (array.Length > 0) ? array : null);
}
